(3265, 277, '_a2_re_3_a2_re_title', 'field_5ef462b80311c'),
(3266, 277, 'a2_re_3_a2_re_des', 'Poka Media tôn trọng đạo đức trong kinh doanh, tuân thủ luật pháp, giữ đúng cam kết, nói được phải làm được nhằm mang tới hiệu quả cao nhất trong công việc.'),
(3267, 277, '_a2_re_3_a2_re_des', 'field_5ef462c50311d'),
(3268, 277, 'a2_re_4_a2_re_title', 'Tốc Độ:'),
(3269, 277, '_a2_re_4_a2_re_title', 'field_5ef462b80311c'),
